mapper.select:
SELECT S.NUM "number", S.ID "id", S.TITLE "title", S.CONTENT "content", S.IMAGE "image", (SELECT TO_CHAR(S.DATE_REGI, 'MM/DD - HH12:MI ') FROM DUAL) "date", S.STATE "state", S.HITS "hits", (SELECT COUNT(*) FROM J_STORY_COMMENT C WHERE C.NUM_STORY=S.NUM AND C.STATE = 'Y') "commentsCNT"
FROM J_STORY "S"
WHERE 1=1 AND S.NUM = #{storyNum}

mapper.update:
UPDATE J_STORY
SET STATE = 'N'
WHERE NUM = #{storyNum}

UPDATE J_STORY_COMMENT
SET STATE = 'N'
WHERE NUM = #{storyCommentNum}

UPDATE J_STORY
SET HITS = HITS+1
WHERE NUM = #{storyNum}

mapper.select:
SELECT S.NUM "number", S.ID "id", S.TITLE "title", S.CONTENT "content", S.IMAGE "image", S.DATE_REGI "date", S.DATE_MODI "dateModi", S.STATE "state", S.HITS "hits"
FROM J_STORY "S"
WHERE 1=1 AND S.NUM = #{storyNum}

mapper.update:
UPDATE J_STORY
SET
	IMAGE=#{storyImagePath}

WHERE NUM = #{storyNum}
